import React from 'react';
import { useTrips } from '../../context/TripContext';
import { Plane } from 'lucide-react';
// Note: To make this functional, you would install a map library like 'react-leaflet' or '@react-google-maps/api'
// and replace this placeholder with a real map component.
// 
const MapView = () => {
    const { activeTrip } = useTrips();

    return (
        <div className="bg-gradient-to-br from-blue-50 to-green-50 rounded-lg p-8 h-full relative overflow-hidden shadow-lg">
            <div className="absolute inset-0 bg-blue-100/30"></div>
            <div className="relative z-10">
                <h3 className="text-xl font-bold mb-4 text-gray-800">Interactive Route Map (Placeholder)</h3>
                <div className="space-y-3">
                    {activeTrip?.destinations.map((dest, index) => (
                        <div key={dest._id} className="flex items-center space-x-3 bg-white/80 backdrop-blur-sm rounded-lg p-3 shadow-sm">
                            <div className="bg-blue-500 text-white rounded-full w-8 h-8 flex items-center justify-center font-bold text-sm">
                                {index + 1}
                            </div>
                            <div className="flex-1">
                                <span className="font-semibold">{dest.name}</span>
                                <span className="text-gray-600 ml-2">Day {dest.day + 1}</span>
                            </div>
                            {index < activeTrip.destinations.length - 1 && (
                                <div className="flex items-center">
                                    <div className="w-8 border-t-2 border-dashed border-blue-300"></div>
                                    <Plane className="w-4 h-4 text-blue-600" />
                                </div>
                            )}
                        </div>
                    ))}
                </div>
                <div className="mt-6 p-4 bg-white/90 backdrop-blur-sm rounded-lg shadow-sm">
                    <div className="flex items-center justify-between">
                        <span className="font-semibold">Total Distance: ~2,847 km</span>
                        <span className="font-semibold">Estimated Travel Time: 12h 30m</span>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default MapView;
