import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Outlet } from 'react-router-dom';
import { TripProvider } from './context/TripContext';
import { AuthProvider, useAuth } from './hooks/useAuth';

import Dashboard from './pages/Dashboard';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import HomePage from './pages/HomePage';
import Navbar from './components/layout/Navbar';

/**
 * A layout component for public-facing pages.
 * It includes the main Navbar and an Outlet to render the specific page content.
 */
const PublicLayout = () => (
  <>
    <Navbar />
    <main>
      <Outlet />
    </main>
  </>
);

/**
 * This component contains the main routing logic for the application.
 * It uses the auth context to determine which routes are accessible.
 */
const AppRoutes = () => {
  const { user } = useAuth();

  return (
    <Routes>
      {/* The root path directs to the dashboard if logged in, otherwise to the public home page. */}
      <Route
        path="/"
        element={user ? <Dashboard /> : <Navigate to="/home" />}
      />

      {/* Public routes that are only accessible when the user is NOT logged in. */}
      {/* These routes are wrapped in a layout that includes the public navbar. */}
      <Route element={<PublicLayout />}>
        <Route
          path="/home"
          element={!user ? <HomePage /> : <Navigate to="/" />}
        />
        <Route
          path="/login"
          element={!user ? <LoginPage /> : <Navigate to="/" />}
        />
        <Route
          path="/register"
          element={!user ? <RegisterPage /> : <Navigate to="/" />}
        />
      </Route>

      {/* Fallback route to redirect any unknown paths to the appropriate home screen. */}
      <Route path="*" element={<Navigate to={user ? '/' : '/home'} />} />
    </Routes>
  );
};

/**
 * The main App component sets up the context providers and router.
 */
function App() {
  return (
    <Router>
      <AuthProvider>
        <TripProvider>
          <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-green-50">
            <AppRoutes />
          </div>
        </TripProvider>
      </AuthProvider>
    </Router>
  );
}

export default App;
