import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { getAllTrips, deleteTrip } from '../services/api';
import ItineraryList from '../components/ItineraryList';

const HomePage = () => {
    // All hooks are now called at the top level of the component.
    const { user } = useAuth();
    const [trips, setTrips] = useState([]);
    const [loading, setLoading] = useState(true);

    // This function will now only fetch trips if a user is logged in.
    const fetchTrips = useCallback(async () => {
        if (!user) {
            setLoading(false);
            return;
        }
        try {
            const { data } = await getAllTrips();
            setTrips(data);
        } catch (error) {
            console.error("Error fetching trips:", error);
        } finally {
            setLoading(false);
        }
    }, [user]); // The user object is now a dependency.

    useEffect(() => {
        fetchTrips();
    }, [fetchTrips]);

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this trip?')) {
            try {
                await deleteTrip(id);
                fetchTrips(); // Refetch trips to update the list
            } catch (error) {
                console.error("Failed to delete trip:", error);
            }
        }
    };

    // Conditional rendering is handled here, after all hooks have been called.
    if (!user) {
        // If user is not logged in, show a public landing page
        return (
            <div className="text-center py-20">
                <h1 className="text-5xl font-bold text-gray-800">Welcome to Travel Planner</h1>
                <p className="text-xl text-gray-600 mt-4">Plan your next adventure with ease.</p>
                <div className="mt-8 space-x-4">
                    <Link to="/login" className="bg-indigo-600 text-white font-bold py-3 px-6 rounded-md hover:bg-indigo-700">
                        Login
                    </Link>
                    <Link to="/signup" className="bg-gray-200 text-gray-800 font-bold py-3 px-6 rounded-md hover:bg-gray-300">
                        Sign Up
                    </Link>
                </div>
            </div>
        );
    }

    // If user is logged in, show their trip dashboard
    return (
        <div className="container mx-auto p-4">
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold">Your Trips</h1>
                <Link to="/planner" className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded">
                    + Create New Trip
                </Link>
            </div>
            {loading ? <p>Loading trips...</p> : <ItineraryList trips={trips} onDelete={handleDelete} />}
        </div>
    );
};

export default HomePage;
