import React from 'react';
import { Calendar, Navigation } from 'lucide-react';

const ViewToggle = ({ viewMode, setViewMode }) => {
    return (
        <div className="bg-white rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold mb-4">View Mode</h3>
            <div className="grid grid-cols-2 gap-2">
                <button
                    onClick={() => setViewMode('itinerary')}
                    className={`p-3 rounded-lg transition-colors ${viewMode === 'itinerary' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
                >
                    <Calendar className="w-5 h-5 mx-auto mb-1" />
                    <div className="text-sm">Itinerary</div>
                </button>
                <button
                    onClick={() => setViewMode('map')}
                    className={`p-3 rounded-lg transition-colors ${viewMode === 'map' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
                >
                    <Navigation className="w-5 h-5 mx-auto mb-1" />
                    <div className="text-sm">Map</div>
                </button>
            </div>
        </div>
    );
};

export default ViewToggle;
