import React from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Header = () => {
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/'); // Redirect to the homepage after logout
    };

    // Style for the active navigation link
    const activeLinkStyle = {
        color: '#4f46e5', // A shade of indigo
        textDecoration: 'underline',
        textUnderlineOffset: '4px',
    };

    return (
        <header className="bg-white shadow-sm sticky top-0 z-50">
            <nav className="container mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex justify-between items-center py-4">
                    <Link to="/" className="text-3xl font-bold text-indigo-600">
                        TravelPlanner
                    </Link>
                    <div className="flex items-center space-x-8">
                        <NavLink 
                            to="/" 
                            className="text-gray-600 hover:text-indigo-600 font-medium transition-colors duration-300" 
                            style={({ isActive }) => isActive ? activeLinkStyle : undefined}
                        >
                            Home
                        </NavLink>
                        {user && (
                             <NavLink 
                                to="/planner" 
                                className="text-gray-600 hover:text-indigo-600 font-medium transition-colors duration-300" 
                                style={({ isActive }) => isActive ? activeLinkStyle : undefined}
                            >
                                Planner
                            </NavLink>
                        )}
                       
                        {user ? (
                            // Show if user is logged in
                            <>
                                <span className="text-gray-700">Hello, {user.username}</span>
                                <button
                                    onClick={handleLogout}
                                    className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded-md transition duration-300"
                                >
                                    Logout
                                </button>
                            </>
                        ) : (
                            // Show if user is logged out
                            <>
                                <Link to="/login" className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded-md transition duration-300">
                                    Login
                                </Link>
                                <Link to="/signup" className="text-gray-600 hover:text-indigo-600 font-medium">
                                    Sign Up
                                </Link>
                            </>
                        )}
                    </div>
                </div>
            </nav>
        </header>
    );
};

export default Header;
