import React from 'react';
import { useTrips } from '../../context/TripContext';
import * as api from '../../api';
import ActivityCard from '../cards/ActivityCard';
import { MapPin } from 'lucide-react';

const ItineraryView = ({ selectedDay, setShowAddDestination }) => {
    const { activeTrip, refreshActiveTrip } = useTrips();

    const getDayDestinations = (day) => {
        return activeTrip?.destinations.filter(dest => dest.day === day) || [];
    };

    const addActivity = async (destinationId, activityData) => {
        try {
            const { data: updatedTrip } = await api.addActivity(activeTrip._id, destinationId, activityData);
            refreshActiveTrip(updatedTrip);
        } catch (error) {
            console.error("Failed to add activity", error);
        }
    };
    
    return (
        <div className="bg-white rounded-xl shadow-lg p-6">
            <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold">Day {selectedDay + 1} Itinerary</h2>
                <div className="text-sm text-gray-600">
                    {new Date(new Date(activeTrip.startDate).getTime() + selectedDay * 24 * 60 * 60 * 1000).toLocaleDateString()}
                </div>
            </div>

            {getDayDestinations(selectedDay).map(destination => (
                <div key={destination._id} className="mb-8 last:mb-0">
                    <div className="flex items-center space-x-3 mb-4">
                        <div className="bg-blue-600 text-white rounded-full w-10 h-10 flex items-center justify-center">
                            <MapPin className="w-5 h-5" />
                        </div>
                        <div>
                            <h3 className="text-xl font-bold text-gray-800">{destination.name}</h3>
                            <p className="text-gray-600">{destination.accommodation}</p>
                        </div>
                    </div>

                    {destination.activities.length > 0 ? (
                        <div className="ml-13 space-y-3">
                            {destination.activities.map(activity => (
                                <ActivityCard key={activity._id} activity={activity} destinationId={destination._id} />
                            ))}
                        </div>
                    ) : (
                        <div className="ml-13 bg-gray-50 rounded-lg p-6 text-center">
                            <p className="text-gray-600 mb-4">No activities planned yet</p>
                            <button
                                onClick={() => addActivity(destination._id, { name: "New Activity", time: "09:00", duration: 2, cost: 0, rating: 4.5 })}
                                className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                            >
                                Add Activity
                            </button>
                        </div>
                    )}

                    {destination.notes && (
                        <div className="ml-13 mt-4 bg-yellow-50 border-l-4 border-yellow-400 p-4 rounded">
                            <p className="text-sm text-gray-700"><strong>Note:</strong> {destination.notes}</p>
                        </div>
                    )}
                </div>
            ))}

            {getDayDestinations(selectedDay).length === 0 && (
                <div className="text-center py-12">
                    <MapPin className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                    <h3 className="text-lg font-semibold text-gray-600 mb-2">No destinations for this day</h3>
                    <p className="text-gray-500 mb-4">Start by adding a destination to your itinerary</p>
                    <button
                        onClick={() => setShowAddDestination(true)}
                        className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                    >
                        Add Destination
                    </button>
                </div>
            )}
        </div>
    );
};

export default ItineraryView;
