import React from 'react';
import { useTrips } from '../../context/TripContext';
import * as api from '../../api';
import { Clock, DollarSign, Star, Trash2 } from 'lucide-react';

const ActivityCard = ({ activity, destinationId }) => {
    const { activeTrip, refreshActiveTrip } = useTrips();

    const handleDelete = async () => {
        if (window.confirm("Are you sure you want to delete this activity?")) {
            try {
                const { data: updatedTrip } = await api.deleteActivity(activeTrip._id, destinationId, activity._id);
                refreshActiveTrip(updatedTrip);
            } catch (error) {
                console.error("Failed to delete activity", error);
            }
        }
    };

    return (
        <div className="bg-white rounded-lg shadow-sm border p-4 hover:shadow-md transition-shadow">
            <div className="flex items-start justify-between">
                <div className="flex-1">
                    <h4 className="font-semibold text-gray-800">{activity.name}</h4>
                    <div className="flex items-center space-x-4 mt-2 text-sm text-gray-600">
                        <div className="flex items-center space-x-1"><Clock className="w-4 h-4" /><span>{activity.time} ({activity.duration}h)</span></div>
                        <div className="flex items-center space-x-1"><DollarSign className="w-4 h-4" /><span>${activity.cost}</span></div>
                        <div className="flex items-center space-x-1"><Star className="w-4 h-4 text-yellow-500" /><span>{activity.rating}</span></div>
                    </div>
                </div>
                <button onClick={handleDelete} className="text-gray-400 hover:text-red-500 transition-colors">
                    <Trash2 className="w-4 h-4" />
                </button>
            </div>
        </div>
    );
};

export default ActivityCard;
