import React, { useState, useEffect } from 'react';

/**
 * Displays and allows editing of a single trip's details.
 * @param {object} props - The component props.
 * @param {object} props.trip - The trip object to display.
 * @param {Function} props.onSave - The function to call to save updated trip data.
 */
const TripDetails = ({ trip, onSave }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState(trip);

    // When the trip prop changes, update the component's state.
    // This is important if the user navigates from one trip's details to another.
    useEffect(() => {
        setFormData(trip);
    }, [trip]);

    // Handle changes in form inputs during editing.
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    // Handle the save action when the form is submitted.
    const handleSave = (e) => {
        e.preventDefault();
        onSave(formData); // Pass the updated data to the parent component.
        setIsEditing(false); // Exit editing mode.
    };

    // Render the edit form when in editing mode.
    if (isEditing) {
        return (
            <form onSubmit={handleSave} className="space-y-4">
                <h2 className="text-2xl font-bold mb-4">Edit Trip</h2>
                <div>
                    <label className="block text-gray-700 font-bold mb-1">Title</label>
                    <input type="text" name="title" value={formData.title} onChange={handleChange} className="shadow-sm appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring focus:border-indigo-500" />
                </div>
                <div>
                    <label className="block text-gray-700 font-bold mb-1">Description</label>
                    <textarea name="description" value={formData.description} onChange={handleChange} rows="4" className="shadow-sm appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring focus:border-indigo-500" />
                </div>
                <div className="flex space-x-4">
                    <button type="submit" className="bg-indigo-600 text-white font-bold py-2 px-4 rounded w-full hover:bg-indigo-700 transition-colors">Save Changes</button>
                    <button type="button" onClick={() => setIsEditing(false)} className="bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded w-full hover:bg-gray-400 transition-colors">Cancel</button>
                </div>
            </form>
        );
    }
    
    // Render the display view when not editing.
    return (
        <div>
            <div className="flex justify-between items-center mb-4">
                <h2 className="text-3xl font-bold">{trip.title}</h2>
                <button onClick={() => setIsEditing(true)} className="font-medium text-indigo-600 hover:underline">Edit</button>
            </div>
            <p className="text-gray-600 mb-4">{trip.description || "No description."}</p>
            <p className="font-semibold mb-2">Dates: {new Date(trip.startDate).toLocaleDateString()} to {new Date(trip.endDate).toLocaleDateString()}</p>
            <h3 className="text-xl font-semibold mt-6 mb-2">Route</h3>
            {trip.route && trip.route.length > 0 ? (
                <ul className="list-decimal list-inside space-y-1">
                    {trip.route.map((location, index) => (
                        <li key={index}>{location.name}</li>
                    ))}
                </ul>
            ) : (
                <p className="text-gray-500">No route planned yet. Add locations on the map to build your itinerary!</p>
            )}
        </div>
    );
};

export default TripDetails;
