import React, { useState } from 'react';
import { useTrips } from '../../context/TripContext';
import * as api from '../../api';

const AddDestinationForm = ({ setShowAddDestination, selectedDay }) => {
    const { activeTrip, refreshActiveTrip } = useTrips();
    const [formData, setFormData] = useState({ name: '', day: selectedDay, lat: 0, lng: 0 });

    const getTotalDays = () => {
        if (!activeTrip || !activeTrip.startDate || !activeTrip.endDate) return 1;
        const start = new Date(activeTrip.startDate);
        const end = new Date(activeTrip.endDate);
        return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (formData.name.trim()) {
            try {
                const { data: updatedTrip } = await api.addDestination(activeTrip._id, formData);
                refreshActiveTrip(updatedTrip);
                setShowAddDestination(false);
            } catch (error) {
                console.error("Failed to add destination", error);
            }
        }
    };

    return (
        <div className="bg-white rounded-lg shadow-lg p-6">
            <h3 className="text-lg font-semibold mb-4">Add New Destination</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-sm font-medium mb-1">Destination</label>
                    <input
                        type="text"
                        value={formData.name}
                        onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                        className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        placeholder="e.g., Tokyo, Japan"
                        required
                    />
                </div>
                <div>
                    <label className="block text-sm font-medium mb-1">Day</label>
                    <select
                        value={formData.day}
                        onChange={(e) => setFormData({ ...formData, day: parseInt(e.target.value) })}
                        className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    >
                        {Array.from({ length: getTotalDays() }, (_, i) => (
                            <option key={i} value={i}>Day {i + 1}</option>
                        ))}
                    </select>
                </div>
                <div className="flex space-x-2">
                    <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors">
                        Add Destination
                    </button>
                    <button
                        type="button"
                        onClick={() => setShowAddDestination(false)}
                        className="bg-gray-300 text-gray-700 px-4 py-2 rounded-lg hover:bg-gray-400 transition-colors"
                    >
                        Cancel
                    </button>
                </div>
            </form>
        </div>
    );
};

export default AddDestinationForm;
