import React, { useState } from 'react';
import { useTrips } from '../context/TripContext';
import * as api from '../api';

import Header from '../components/layout/Header';
import TripOverview from '../components/sidebar/TripOverview';
import ViewToggle from '../components/sidebar/ViewToggle';
import DayNavigation from '../components/sidebar/DayNavigation';
import QuickActions from '../components/sidebar/QuickActions';
import MainContent from '../components/layout/MainContent';
import { MapPin } from 'lucide-react';

const Dashboard = () => {
    const { activeTrip, setActiveTrip, setTrips, trips, loading } = useTrips();
    const [selectedDay, setSelectedDay] = useState(0);
    const [viewMode, setViewMode] = useState('itinerary'); // 'itinerary' or 'map'

    const addNewTrip = async () => {
        try {
            const { data: newTrip } = await api.createTrip({ name: "New Trip" });
            setTrips([...trips, newTrip]);
            setActiveTrip(newTrip);
            setSelectedDay(0);
        } catch (error) {
            console.error("Failed to create new trip:", error);
        }
    };

    if (loading) {
        return <div className="text-center p-10">Loading your adventures...</div>;
    }

    if (!activeTrip) {
        return (
            <div className="min-h-screen p-6">
                <div className="max-w-4xl mx-auto text-center">
                    <h1 className="text-4xl font-bold text-gray-800 mb-8">Travel Itinerary Planner</h1>
                    <div className="bg-white rounded-xl shadow-lg p-8">
                        <MapPin className="w-16 h-16 text-blue-600 mx-auto mb-4" />
                        <h2 className="text-2xl font-semibold mb-4">Start Planning Your Adventure</h2>
                        <p className="text-gray-600 mb-6">Create detailed itineraries, collaborate with friends, and visualize your journey on interactive maps.</p>
                        <button
                            onClick={addNewTrip}
                            className="bg-blue-600 text-white px-8 py-3 rounded-lg hover:bg-blue-700 transition-colors font-semibold"
                        >
                            Create New Trip
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <>
            <Header />
            <div className="max-w-7xl mx-auto px-6 py-6">
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                    {/* Left Sidebar */}
                    <div className="lg:col-span-1 space-y-6">
                        <TripOverview />
                        <ViewToggle viewMode={viewMode} setViewMode={setViewMode} />
                        {viewMode === 'itinerary' && (
                            <DayNavigation selectedDay={selectedDay} setSelectedDay={setSelectedDay} />
                        )}
                        <QuickActions />
                    </div>

                    {/* Main Content */}
                    <div className="lg:col-span-2">
                        <MainContent viewMode={viewMode} selectedDay={selectedDay} />
                    </div>
                </div>
            </div>
        </>
    );
};

export default Dashboard;
