import React, { useState } from 'react';
import AddDestinationForm from '../forms/AddDestinationForm';
import ItineraryView from '../main/ItineraryView';
import MapView from '../main/MapView';

const MainContent = ({ viewMode, selectedDay }) => {
    const [showAddDestination, setShowAddDestination] = useState(false);

    return (
        <div className="space-y-6">
            {showAddDestination && <AddDestinationForm setShowAddDestination={setShowAddDestination} selectedDay={selectedDay} />}

            {!showAddDestination && viewMode === 'map' && <MapView />}

            {!showAddDestination && viewMode === 'itinerary' && (
                <ItineraryView selectedDay={selectedDay} setShowAddDestination={setShowAddDestination} />
            )}
        </div>
    );
};

export default MainContent;
