import React from 'react';
import { useTrips } from '../../context/TripContext';

const DayNavigation = ({ selectedDay, setSelectedDay }) => {
    const { activeTrip } = useTrips();

    const getDayDestinations = (day) => {
        return activeTrip?.destinations.filter(dest => dest.day === day) || [];
    };

    const getTotalDays = () => {
        if (!activeTrip || !activeTrip.startDate || !activeTrip.endDate) return 1;
        const start = new Date(activeTrip.startDate);
        const end = new Date(activeTrip.endDate);
        return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
    };
    
    return (
        <div className="bg-white rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold mb-4">Days</h3>
            <div className="space-y-2">
                {Array.from({ length: getTotalDays() }, (_, day) => (
                    <button
                        key={day}
                        onClick={() => setSelectedDay(day)}
                        className={`w-full text-left p-3 rounded-lg transition-colors ${selectedDay === day ? 'bg-blue-600 text-white' : 'bg-gray-50 text-gray-700 hover:bg-gray-100'}`}
                    >
                        <div className="font-semibold">Day {day + 1}</div>
                        <div className="text-sm opacity-75">
                            {getDayDestinations(day).map(dest => dest.name).join(', ') || 'No destinations'}
                        </div>
                    </button>
                ))}
            </div>
        </div>
    );
};

export default DayNavigation;
