import React from 'react';
import { useTrips } from '../../context/TripContext';

const TripOverview = () => {
    const { activeTrip } = useTrips();

    const getTotalCost = () => {
        return activeTrip?.destinations.reduce((total, dest) =>
            total + dest.activities.reduce((destTotal, activity) => destTotal + activity.cost, 0), 0
        ) || 0;
    };

    const getTotalDays = () => {
        if (!activeTrip || !activeTrip.startDate || !activeTrip.endDate) return 1;
        const start = new Date(activeTrip.startDate);
        const end = new Date(activeTrip.endDate);
        return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
    };

    return (
        <div className="bg-white rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold mb-4">Trip Overview</h3>
            <div className="space-y-3">
                <div className="flex items-center justify-between">
                    <span className="text-gray-600">Duration</span>
                    <span className="font-semibold">{getTotalDays()} days</span>
                </div>
                <div className="flex items-center justify-between">
                    <span className="text-gray-600">Destinations</span>
                    <span className="font-semibold">{activeTrip.destinations.length}</span>
                </div>
                <div className="flex items-center justify-between">
                    <span className="text-gray-600">Total Cost</span>
                    <span className="font-semibold text-green-600">${getTotalCost()}</span>
                </div>
            </div>
        </div>
    );
};

export default TripOverview;
