import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { TripProvider } from './context/TripContext';
import { AuthProvider, useAuth } from './hooks/useAuth';

import Dashboard from './pages/Dashboard';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import HomePage from './pages/HomePage';

// A wrapper for <Route> that redirects to the login screen if you're not yet authenticated.
// This component now correctly uses the auth context provided by AuthProvider.
function ProtectedRoute({ children }) {
    const auth = useAuth();
    // A small check to see if auth context is still loading
    if (auth.user === undefined) {
        return null; // or a loading spinner
    }
    return auth.user ? children : <Navigate to="/login" />;
}

function App() {
  return (
    // The Router must be the outermost component to provide routing context
    // to all hooks and components that need it, like useAuth and ProtectedRoute.
    <Router>
      <AuthProvider>
        <TripProvider>
          <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-green-50">
            <Routes>
              <Route path="/home" element={<HomePage />} />
              <Route path="/login" element={<LoginPage />} />
              <Route path="/register" element={<RegisterPage />} />

              {/* Protected Route for the main dashboard */}
              <Route 
                path="/" 
                element={
                  <ProtectedRoute>
                    <Dashboard />
                  </ProtectedRoute>
                } 
              />

              {/* Redirect any other path to the home page as a fallback */}
              <Route path="*" element={<Navigate to="/home" />} />
            </Routes>
          </div>
        </TripProvider>
      </AuthProvider>
    </Router>
  );
}

export default App;
