import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { getAllTrips, deleteTrip } from '../services/api';
import ItineraryList from '../components/ItineraryList';

const HomePage = () => {
    const [trips, setTrips] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchTrips = useCallback(async () => {
        try {
            setLoading(true);
            const response = await getAllTrips();
            setTrips(response.data);
            setError(null);
        } catch (err) {
            setError("Failed to load trips. Please try again later.");
            console.error("Error fetching trips:", err);
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchTrips();
    }, [fetchTrips]);

    const handleDelete = async (id) => {
        // A simple confirmation dialog. For a better UX, consider a custom modal.
        if (window.confirm('Are you sure you want to delete this trip?')) {
            try {
                await deleteTrip(id);
                // Refetch trips to update the list after deletion.
                fetchTrips();
            } catch (error) {
                setError("Failed to delete trip.");
                console.error("Failed to delete trip:", error);
            }
        }
    };

    return (
        <div>
            <div className="flex justify-between items-center mb-8">
                <h1 className="text-4xl font-bold text-gray-800">Your Trips</h1>
                <Link to="/planner" className="inline-flex items-center justify-center px-5 py-3 border border-transparent text-base font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700">
                    + Plan New Trip
                </Link>
            </div>
            {loading && <p className="text-center">Loading your adventures...</p>}
            {error && <p className="text-center text-red-500">{error}</p>}
            {!loading && !error && <ItineraryList trips={trips} onDelete={handleDelete} />}
        </div>
    );
};

export default HomePage;
