import axios from 'axios';

// The base URL for the backend API.
// During development, this will be http://localhost:5000.
// In production, this should be the URL of your deployed backend.
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api/trips';

const api = axios.create({
    baseURL: API_URL,
});

/**
 * Fetches all trips from the server.
 * @returns {Promise<AxiosResponse<any>>} A promise that resolves to the server response.
 */
export const getAllTrips = () => api.get('/');

/**
 * Fetches a single trip by its ID.
 * @param {string} id The ID of the trip to fetch.
 * @returns {Promise<AxiosResponse<any>>} A promise that resolves to the server response.
 */
export const getTripById = (id) => api.get(`/${id}`);

/**
 * Creates a new trip.
 * @param {object} tripData The data for the new trip.
 * @returns {Promise<AxiosResponse<any>>} A promise that resolves to the server response.
 */
export const createTrip = (tripData) => api.post('/', tripData);

/**
 * Updates an existing trip.
 * @param {string} id The ID of the trip to update.
 * @param {object} tripData The new data for the trip.
 * @returns {Promise<AxiosResponse<any>>} A promise that resolves to the server response.
 */
export const updateTrip = (id, tripData) => api.patch(`/${id}`, tripData);

/**
 * Deletes a trip.
 * @param {string} id The ID of the trip to delete.
 * @returns {Promise<AxiosResponse<any>>} A promise that resolves to the server response.
 */
export const deleteTrip = (id) => api.delete(`/${id}`);

export default api;
