import axios from 'axios';

const API = axios.create({ baseURL: '/api' });

// We would add an interceptor to add the auth token to every request
API.interceptors.request.use((req) => {
    const userInfo = localStorage.getItem('userInfo');
    if (userInfo) {
        req.headers.Authorization = `Bearer ${JSON.parse(userInfo).token}`;
    }
    return req;
});

// Trip API calls
export const fetchTrips = () => API.get('/trips');
export const createTrip = (newTrip) => API.post('/trips', newTrip);
export const deleteTrip = (id) => API.delete(`/trips/${id}`);
export const addDestination = (tripId, destData) => API.post(`/trips/${tripId}/destinations`, destData);
export const deleteDestination = (tripId, destId) => API.delete(`/trips/${tripId}/destinations/${destId}`);
export const addActivity = (tripId, destId, activityData) => API.post(`/trips/${tripId}/destinations/${destId}/activities`, activityData);
export const deleteActivity = (tripId, destId, activityId) => API.delete(`/trips/${tripId}/destinations/${destId}/activities/${activityId}`);
