import React from 'react';
import { useTrips } from '../../context/TripContext';
import { Calendar, Users, Share2 } from 'lucide-react';

const Header = () => {
    const { activeTrip } = useTrips();
    if (!activeTrip) return null;

    const formatDate = (dateString) => new Date(dateString).toLocaleDateString();

    return (
        <div className="bg-white shadow-sm border-b">
            <div className="max-w-7xl mx-auto px-6 py-4">
                <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                        <h1 className="text-2xl font-bold text-gray-800">{activeTrip.name}</h1>
                        <div className="flex items-center space-x-2 text-sm text-gray-600">
                            <Calendar className="w-4 h-4" />
                            <span>{formatDate(activeTrip.startDate)} to {formatDate(activeTrip.endDate)}</span>
                        </div>
                    </div>
                    <div className="flex items-center space-x-4">
                        <div className="flex items-center space-x-2">
                            <Users className="w-4 h-4 text-gray-600" />
                            <span className="text-sm text-gray-600">{activeTrip.collaborators?.length || 0} collaborators</span>
                        </div>
                        <button className="flex items-center space-x-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors">
                            <Share2 className="w-4 h-4" />
                            <span>Share</span>
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Header;
