import React from 'react';
import { Plus, Users } from 'lucide-react';

const QuickActions = () => {
    // Note: setShowAddDestination is managed in MainContent now
    // This component would need context or props to trigger the form display
    return (
        <div className="bg-white rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold mb-4">Quick Actions</h3>
            <div className="space-y-2">
                <p className="text-sm text-gray-500">Use the buttons in the itinerary view to add destinations or activities.</p>
                <button className="w-full flex items-center space-x-2 bg-purple-600 text-white p-3 rounded-lg hover:bg-purple-700 transition-colors">
                    <Users className="w-4 h-4" />
                    <span>Invite Collaborators</span>
                </button>
            </div>
        </div>
    );
};

export default QuickActions;
